INSERT INTO halls(name, row_count, place_count, description)
VALUES ('Moon', 12, 17,
        'Кинопространство mooon в ТРЦ Dana Mall — беспроигрышный вариант для полноценного расслабления и непревзойдённого отдыха, которое подарит своим гостям настоящее удовольствие от просмотра фильмов. Только здесь обыкновенный поход в кино становится важным событием, ведь кинопространство mooon со своими запоминающимися залами — уютнейшее место, куда вновь и вновь хочется возвращаться с любимым человеком, большой компанией или чтобы побыть наедине с самим собой.'),
       ('Silver Screen', 9, 12,
        'В Silver Screen вас ждут 6 высокотехнологичных кинозалов с удобными креслами, ярким дизайном и огромными экранами. Смотрите кино с комфортом в одиночку, вдвоём или компанией: в наших залах есть возможности для кинолюбителей любого формата. Кожаные сиденья, кресла-реклайнеры с подушками и пледами, которые можно настраивать как вам удобно, а также мягкие романтичные диваны для пар: Silver Screen — это место, где кинопросмотр превращается в настоящее удовольствие, забыть которое невозможно!.'),
       ('Skyline', 10, 14,
        'В SKYLINE Cinema, расположенном на трех уровнях, вас ждут громкие премьеры и яркие мероприятия. SKYLINE Cinema расположен в сердце города — в торговом центре Galileo, всего в нескольких шагах от станций метро и железнодорожного вокзала. Наслаждайтесь киномаркетом с вкуснейшим попкорном и SKYLINE BAR с уютной lounge-зоной и потрясающим панорамным видом на город. Мы гарантируем высокий уровень сервиса и атмосферу уюта, чтобы каждый визит оставлял только положительные эмоции.'),
       ('Октябрь', 34, 36,
        'В 1975 году по проекту архитектора В.Малышева на месте бывшего деревянного кинотеатра «Зорка» было закончено строительство роскошного здания кинотеатра «Октябрь», которое и теперь невольно притягивает взгляды жителей и гостей столицы. Открытие кинотеатра в 1975 году сопровождалось премьерой нашумевшего в то время фильма «Блокада».');